
public class Point {
	
 String name;
 int x;
 int y;

public Point (String name, int x, int y) {
	this.name = name;
	this.x = x;
	this.y = y;
}

public Point() {
	// TODO Auto-generated constructor stub
}

public String getName() {
	return name;
}

public void setName(String name) {
	this.name = name;
}

public int getX() {
	return x;
}

public void setX(int x) {
	this.x = x;
}

public int getY() {
	return y;
}

public void setY(int y) {
	this.y = y;
}




}
